package com.example.c052735.simpleexamplemvvm.ui;import android.databinding.DataBindingUtil;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import com.example.c052735.simpleexamplemvvm.R;import com.example.c052735.simpleexamplemvvm.databinding.CommentItemBinding;import com.example.c052735.simpleexamplemvvm.model.Comment;import java.util.List;public class CommentAdapter extends RecyclerView.Adapter<CommentAdapter.CommentViewHolder> {    private List<? extends Comment> mCommentList;    private final CommentClickCallback mCommentClickCallback;    public CommentAdapter(CommentClickCallback commentClickCallback) {        mCommentClickCallback = commentClickCallback;    }    @Override    public CommentViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        CommentItemBinding binding = DataBindingUtil.inflate(LayoutInflater.from(parent.getContext()), R.layout.comment_item, parent, false);        binding.setCallback(mCommentClickCallback);        return new CommentViewHolder(binding);    }    @Override    public void onBindViewHolder(@NonNull CommentAdapter.CommentViewHolder holder, int position) {        holder.binding.setComment(mCommentList.get(position));        holder.binding.executePendingBindings();    }    @Override    public int getItemCount() {        return mCommentList == null ? 0 : mCommentList.size();    }    static class CommentViewHolder extends RecyclerView.ViewHolder {        final CommentItemBinding binding;        CommentViewHolder(CommentItemBinding binding) {            super(binding.getRoot());            this.binding = binding;        }    }}